import { useState, useEffect } from "react";
import { fetchPortfolio, createPortfolio } from "../Api/index";
const Portfolio = () => {
  const [portfolioData, setPortfolioData] = useState({
    portfolioId: 1234,
    title1: "",
    title2: "",
    title3: "",
    title4: "",
    title5: "",
    title6: "",
    title7: "",
    title8: "",
    img1: "",
    img2: "",
    img3: "",
    img4: "",
    img5: "",
    img6: "",
    img7: "",
    img8: "",
  });
  const onload = async () => {
    const { data } = await fetchPortfolio();
    if (data.length === 0) {
      createPortfolio(portfolioData);
    } else {
      const {
        detailsId,
        title1,
        title2,
        title3,
        title4,
        title5,
        title6,
        title7,
        title8,
        img1,
        img2,
        img3,
        img4,
        img5,
        img6,
        img7,
        img8,
      } = data[0];
      setPortfolioData({
        portfolioId: detailsId,
        title1: title1,
        title2: title2,
        title3: title3,
        title4: title4,
        title5: title5,
        title6: title6,
        title7: title7,
        title8: title8,
        img1: img1,
        img2: img2,
        img3: img3,
        img4: img4,
        img5: img5,
        img6: img6,
        img7: img7,
        img8: img8,
      });
    }
  };
  useEffect(() => {
    onload();
  }, []);
  return (
    <div>
      {/* Projects */}
      <div id="projects" className="filter">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <div className="section-title">PROJECTS</div>
              <h2>Projects That We're Proud Of</h2>
            </div>{" "}
            {/* end of col */}
          </div>{" "}
          {/* end of row */}
          <div className="row">
            <div className="col-lg-12">
              {/* Filter */}
              {/* end of button group */}
              <div className="grid">
                <div className="element-item development">
                  <a className="popup-with-move-anim" href="#project-1">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title1}</span>
                    </div>
                    <img
                      src={portfolioData.img1}
                      alt="alternative"
                      className="portfolio-image"
                    />
                  </a>
                </div>

                <div className="element-item development">
                  <a className="popup-with-move-anim" href="#project-2">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title2}</span>
                    </div>
                    <img
                      src={portfolioData.img2}
                      alt="alternative"
                      className="portfolio-image"
                    />
                  </a>
                </div>
                <div className="element-item design development marketing">
                  <a className="popup-with-move-anim" href="#project-3">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title3}</span>
                    </div>
                    <img
                      src={portfolioData.img3}
                      alt="alternative"
                      className="portfolio-image"
                    />
                  </a>
                </div>
                <div className="element-item design development marketing">
                  <a className="popup-with-move-anim" href="#project-4">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title4}</span>
                    </div>
                    <img
                      src={portfolioData.img4}
                      alt="alternative"
                      className="portfolio-image"
                    />
                  </a>
                </div>
                <div className="element-item design development marketing seo">
                  <a className="popup-with-move-anim" href="#project-5">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title5}</span>
                    </div>
                    <img
                      src={portfolioData.img5}
                      alt="alternative"
                      className="portfolio-image"
                    />
                  </a>
                </div>
                <div className="element-item design marketing seo">
                  <a className="popup-with-move-anim" href="#project-6">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title6}</span>
                    </div>
                    <img
                      src={portfolioData.img6}
                      alt="alternative"
                      className="portfolio-image"
                    />
                  </a>
                </div>
                <div className="element-item design marketing">
                  <a className="popup-with-move-anim" href="#project-7">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title7}</span>
                    </div>
                    <img
                      src={portfolioData.img7}
                      alt="alternative"
                      className="portfolio-image"
                    />
                  </a>
                </div>
                <div className="element-item design marketing">
                  <a className="popup-with-move-anim" href="#project-8">
                    <div className="element-item-overlay">
                      <span>{portfolioData.title8}</span>
                    </div>
                    <img
                      className="portfolio-image"
                      src={portfolioData.img8}
                      alt="alternative"
                    />
                  </a>
                </div>
              </div>{" "}
              {/* end of grid */}
              {/* end of filter */}
            </div>{" "}
            {/* end of col */}
          </div>{" "}
          {/* end of row */}
        </div>{" "}
        {/* end of container */}
      </div>{" "}
      {/* end of filter */}
      {/* end of projects */}
      {/* Project Lightboxes */}
      {/* Lightbox */}
      {/* end of lightbox-basic */}
      {/* end of lightbox */}
      {/* end of project lightboxes */}
    </div>
  );
};

export default Portfolio;
