import React from "react";
import Intro from "../../Intro/Intro";
import { useState, useEffect } from "react";

import { fetchIntro, updateIntroPost } from "../../Api/index";

const IntroAdmin = () => {
  const [introData, setIntroData] = useState({
    title: "",
    introId: 1234,
    personName: "",
    description: "",
    image: "",
  });

  const onload = async () => {
    const { data } = await fetchIntro();

    const { title, introId, personName, description, image } = data[0];
    setIntroData({
      title: title,
      introId: tagId,
      personName: personName,
      description: description,
      image: image,
    });
  };

  useEffect(() => {
    onload();
  }, []);

  const handleSubmit = (e) => {
    updateIntroPost(introData);
  };

  return (
    <div className="tagline-part container">
      <p>
        <button
          class="btn btn-primary"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#collapseintro"
          aria-expanded="false"
          aria-controls="collapseintro"
        >
          Intro preview
        </button>
      </p>
      <div class="collapse preview" id="collapseintro">
        <Intro />
      </div>
      <form autoComplete="off" noValidate onSubmit={handleSubmit}>
        <div className="custom-file">
          <input
            type="file"
            className="custom-file-input"
            id="inputGroupFile01"
            aria-describedby="inputGroupFileAddon01"
          />
          <label className="custom-file-label" htmlFor="inputGroupFile01">
            Choose file
          </label>
        </div>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="title"
            aria-label="title"
            value={introData.title}
          />

          <input
            type="text"
            className="form-control"
            placeholder="Person Name"
            aria-label="person name"
            value={introData.personName}
          />
        </div>
        <div class="input-group">
          <span class="input-group-text">Description</span>
          <textarea
            class="form-control"
            aria-label="With textarea"
            value={introData.description}
          ></textarea>
        </div>
        <button type="button" class="btn btn-outline-primary submit_button">
          Set
        </button>
      </form>
    </div>
  );
};

export default IntroAdmin;
