import { useState, useEffect } from "react";
import { fetchContact, updateContactPost } from "../../Api/index";

const ContactAdmin = () => {
  const [contactData, setContactData] = useState({
    contactId: 1234,
    address: "",
    phone1: "",
    phone2: "",
    email: "",
    copyright: "",
    brainbox: "",
  });

  const onload = async () => {
    const { data } = await fetchContact();

    const { address, phone1, phone2, email, copyright, brainbox } = data[0];
    setContactData({
      contactId: 1234,
      address: address,
      phone1: phone1,
      phone2: phone2,
      email: email,
      copyright: copyright,
      brainbox: brainbox,
    });
  };
  useEffect(() => {
    onload();
  }, []);
  const handleSubmit = async () => {
    updateContactPost(contactData);
  };
  return (
    <div className="tagline-part container">
      <h3>Contact</h3>
      <form autoComplete="off" noValidate onSubmit={handleSubmit}>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Address"
            aria-label="title"
          />

          <input
            type="text"
            className="form-control"
            placeholder="Phone Number"
            aria-label="Phone Number"
          />
          <input
            type="text"
            className="form-control"
            placeholder="Another Phone Number"
            aria-label="Another Phone Number"
          />
        </div>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Email"
            aria-label="Email"
          />
          <input
            type="text"
            className="form-control"
            placeholder="Copyright Text"
            aria-label="Copyright Text"
          />
        </div>
        <div class="input-group">
          <textarea
            class="form-control"
            placeholder="Few Words About Brainboxbd
"
            aria-label="With textarea"
          ></textarea>
        </div>
        <button type="submit" class="btn btn-outline-primary submit_button">
          Set
        </button>
      </form>
    </div>
  );
};

export default ContactAdmin;
