import React from "react";
import Navbar from "./Navbar";
import FileBase from "react-file-base64";
import "./dashboard.css";
import { useHistory } from "react-router-dom";

import { fetchPortfolio, updatePortfolioPost } from "../../Api/index";
import { useState, useEffect } from "react";
const history = useHistory();
function checkuser() {
  const user = JSON.parse(localStorage.getItem("users"));
  const token = localStorage.getItem("jwtauth");
  if (!token) {
    history.push("/login");
  }
}
const PortfolioAdmin = () => {
  const [portfolioData, setPortfolioData] = useState({
    portfolioId: 1234,
    title1: "",
    title2: "",
    title3: "",
    title4: "",
    title5: "",
    title6: "",
    title7: "",
    title8: "",
    img1: "",
    img2: "",
    img3: "",
    img4: "",
    img5: "",
    img6: "",
    img7: "",
    img8: "",
  });
  const onload = async () => {
    const { data } = await fetchPortfolio();
    console.log(data, "Details ");

    const {
      detailsId,
      title1,
      title2,
      title3,
      title4,
      title5,
      title6,
      title7,
      title8,
      img1,
      img2,
      img3,
      img4,
      img5,
      img6,
      img7,
      img8,
    } = data[0];
    setPortfolioData({
      portfolioId: detailsId,
      title1: title1,
      title2: title2,
      title3: title3,
      title4: title4,
      title5: title5,
      title6: title6,
      title7: title7,
      title8: title8,
      img1: img1,
      img2: img2,
      img3: img3,
      img4: img4,
      img5: img5,
      img6: img6,
      img7: img7,
      img8: img8,
    });
  };
  useEffect(() => {
    onload();
    checkuser();
  }, []);
  const handleSubmit = (e) => {
    updatePortfolioPost(portfolioData);
  };
  return (
    <div>
      <Navbar />
      <div class="container container_bottom">
        <h2>Set Your Projects Here</h2>

        <form autoComplete="off" noValidate onSubmit={handleSubmit}>
          <div class="row ">
            <div class="col-sm card">
              <img src={portfolioData.img1} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img1: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title1}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title1: e.target.value,
                  });
                }}
              />
            </div>
            <div class="col-sm card">
              <img src={portfolioData.img2} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img2: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title2}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title2: e.target.value,
                  });
                }}
              />
            </div>
            <div class="col-sm card">
              <img src={portfolioData.img3} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img3: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title3}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title3: e.target.value,
                  });
                }}
              />
            </div>
          </div>

          {/* === */}
          <div class="row ">
            <div class="col-sm card">
              <img src={portfolioData.img4} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img4: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title4}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title4: e.target.value,
                  });
                }}
              />
            </div>
            <div class="col-sm card">
              <img src={portfolioData.img5} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img5: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title5}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title5: e.target.value,
                  });
                }}
              />
            </div>
            <div class="col-sm card">
              <img src={portfolioData.img6} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img6: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title6}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title6: e.target.value,
                  });
                }}
              />
            </div>
          </div>
          {/* === */}
          <div class="row ">
            <div class="col-sm card">
              <img src={portfolioData.img7} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img7: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title7}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title7: e.target.value,
                  });
                }}
              />
            </div>
            <div class="col-sm card">
              <img src={portfolioData.img8} class="img-responsive" />
              <div class="col-sm pro">
                <FileBase
                  type="file"
                  multipla={false}
                  onDone={({ base64 }) =>
                    setPortfolioData({ ...portfolioData, img8: base64 })
                  }
                />
              </div>
              <input
                type="text"
                class="form-control"
                placeholder="Titile of your project"
                aria-label="Sizing example input"
                aria-describedby="inputGroup-sizing-sm"
                value={portfolioData.title8}
                onChange={(e) => {
                  setPortfolioData({
                    ...portfolioData,
                    title8: e.target.value,
                  });
                }}
              />
            </div>
            <div class="col-sm  button_submit">
              <button
                type="submit"
                class="btn btn-secondary btn-lg button_submit_b"
              >
                Set
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PortfolioAdmin;
