import React from "react";
import Intro from "../../Intro/Intro";
import { useState, useEffect } from "react";

import { fetchIntro, updateIntroPost } from "../../Api/index";

const IntroAdmin = () => {
  const [taglineData, setTaglineData] = useState({
    title: "",
    tagId: 1234,
    personName: "",
    description: "",
    image: "",
  });

  const onload = async () => {
    const { data } = await fetchTagline();

    const { title, tagId, changableText, description, image } = data[0];
    setTaglineData({
      title: title,
      tagId: tagId,
      changableText: changableText,
      description: description,
      image: image,
    });
  };

  return (
    <div className="tagline-part container">
      <p>
        <button
          class="btn btn-primary"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#collapseintro"
          aria-expanded="false"
          aria-controls="collapseintro"
        >
          Intro preview
        </button>
      </p>
      <div class="collapse preview" id="collapseintro">
        <Intro />
      </div>
      <form>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="title"
            aria-label="title"
          />

          <input
            type="text"
            className="form-control"
            placeholder="Person Name"
            aria-label="person name"
          />
        </div>
        <div class="input-group">
          <span class="input-group-text">Description</span>
          <textarea class="form-control" aria-label="With textarea"></textarea>
        </div>
        <button type="button" class="btn btn-outline-primary submit_button">
          Set
        </button>
      </form>
    </div>
  );
};

export default IntroAdmin;
