import Stage from "../../Stage/Stage";
import { useState, useEffect } from "react";

import { fetchStage, updateStagePost } from "../../Api/index";

const StageAdmin = () => {
  const [stageData, setStageData] = useState({
    stageId: 1234,
    des1: "",
    des2: "",
    des3: "",
  });

  const onload = async () => {
    const { data } = await fetchStage();
    console.log(data, "stage ");

    const { stageId, des1, des2, des3 } = data[0];
    setStageData({
      stageId: stageId,
      des1: des1,
      des2: des2,
      des3: des3,
    });
  };
  useEffect(() => {
    onload();
  }, []);
  const handleSubmit = (e) => {
    updateStagePost(stageData);
  };
  return (
    <div className="tagline-part container">
      <p>
        <button
          class="btn btn-primary"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#collapseStage"
          aria-expanded="false"
          aria-controls="collapseStage"
        >
          Stage preview
        </button>
      </p>

      <div class="collapse preview" id="collapseStage">
        <Stage />
      </div>
      <form autoComplete="off" noValidate onSubmit={handleSubmit}>
        <div class="container">
          <div class="row">
            <div class="col-sm">
              <div class="input-group">
                <span class="input-group-text">Description</span>
                <textarea
                  class="form-control"
                  aria-label="With textarea"
                  value={stageData.des1}
                  onChange={(e) => {
                    setIntroData({ ...stageData, des1: e.target.value });
                  }}
                ></textarea>
              </div>
            </div>
            <div class="col-sm">
              <div class="input-group">
                <span class="input-group-text">Description</span>
                <textarea
                  class="form-control"
                  aria-label="With textarea"
                ></textarea>
              </div>
            </div>
            <div class="col-sm">
              <div class="input-group">
                <span class="input-group-text">Description</span>
                <textarea
                  class="form-control"
                  aria-label="With textarea"
                ></textarea>
              </div>
            </div>
          </div>
        </div>
        <button type="button" class="btn btn-outline-primary submit_button">
          Set
        </button>
      </form>
    </div>
  );
};

export default StageAdmin;
