import { useState, useEffect } from "react";
import LightSpeed from "react-reveal/LightSpeed";
import Typical from "react-typical";
import { fetchTagline } from "../Api/index";
const Tagline = () => {
  const [taglineData, setTaglineData] = useState({
    title: "",
    tagId: 1234,
    changableText: [],
    description: "",
    image: "",
  });
  const onload = async () => {
    const { data } = await fetchTagline();

    const { title, tagId, changableText, description, image } = data[0];
    setTaglineData({
      title: title,
      tagId: tagId,
      changableText: changableText.split(","),
      description: description,
      image: image,
    });
  };
  useEffect(() => {
    onload();
  }, []);

  console.log(taglineData.changableText);
  return (
    <div>
      <header
        id="header"
        className="header"
        // style={{
        //   background: `linear-gradient((rgba(0, 0, 0, 0.3), rgba(0, 0, 0, 0.5)), url("https://cdn.pixabay.com/photo/2015/01/20/12/51/mobile-605422_960_720.jpg") center center no-repeat ;`,
        //   backgroundSize: "cover",
        // }}
      >
        <div className="header-content">
          <div className="container">
            <div className="row">
              <div className="col-lg-12">
                <LightSpeed>
                  <div className="text-container">
                    <h1>
                      {taglineData.title}{" "}
                      <span>
                        <Typical
                          className="text_rotate"
                          steps={[
                            taglineData.changableText[0],
                            2000,
                            taglineData.changableText[1],
                            2000,
                            taglineData.changableText[2],
                            2000,
                          ]}
                          loop={Infinity}
                          wrapper="p"
                        />
                      </span>
                    </h1>
                    <p className="p-heading p-large">
                      {taglineData.description}
                    </p>
                    <a className="btn-solid-lg page-scroll" href="#intro">
                      DISCOVER
                    </a>
                  </div>
                </LightSpeed>
              </div>
            </div>
          </div>
        </div>
      </header>
    </div>
  );
};

export default Tagline;
