import React, { useState, useEffect } from "react";
import FileBase from "react-file-base64";
import { fetchService, deleteService, createSerice } from "../../Api/index";

const ServiceAdmin = () => {
  useEffect(() => {
    onload();
  }, []);
  let service = [];
  const [postData, setPostData] = useState({
    title: "",
    description: "",
    image: "",
  });

  const onload = async () => {
    const { data } = await fetchService();
    service = data;
    console.log(service, "service");
  };

  const handleSubmit = (e) => {
    createSerice(postData);
  };
  return (
    <div className="tagline-part container ">
      <p>
        <button
          class="btn btn-primary"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#collapseService"
          aria-expanded="false"
          aria-controls="collapseService"
        >
          Service preview
        </button>
      </p>
      <div class="collapse preview" id="collapseService">
        <div class="container">
          <div class="row">
            {/* {service.map((s) => (
              <div class="col-sm" key={s._id}>
                <p>{s.title}</p>
                <button type="button" class="btn btn-secondary btn-sm">
                  Delete
                </button>
              </div>
            ))} */}
          </div>
        </div>
      </div>
      <form autoComplete="off" noValidate onSubmit={handleSubmit}>
        <div>
          <div className="custom-file">
            <FileBase
              type="file"
              multipla={false}
              onDone={({ base64 }) =>
                setPostData({ ...postData, image: base64 })
              }
            />
          </div>
        </div>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="service name"
            aria-label="service name"
            value={postData.title}
            onChange={(e) => {
              setPostData({ ...postData, title: e.target.value });
            }}
          />
        </div>
        <div class="input-group">
          <span class="input-group-text">Service Description</span>
          <textarea
            class="form-control"
            aria-label="With textarea"
            value={postData.description}
            onChange={(e) => {
              setPostData({ ...postData, description: e.target.value });
            }}
          ></textarea>
        </div>
        <button type="submit" class="btn btn-outline-primary submit_button">
          Set
        </button>
      </form>
    </div>
  );
};

export default ServiceAdmin;
