import React from "react";
import Navbar from "./Navbar";
import FileBase from "react-file-base64";
import "./dashboard.css";
import { useHistory } from "react-router-dom";

import { fetchPortfolio, createPortfolio } from "../../Api/index";
import { useState, useEffect } from "react";
import Loader from "../../Loader";

const PortfolioAdmin = () => {
  const history = useHistory();
  const [load, setLoad] = useState(0);
  const [portfolioData, setPortfolioData] = useState({
    title: "",
    photo: "",
  });

  function checkuser() {
    const user = JSON.parse(localStorage.getItem("users"));
    const token = localStorage.getItem("jwtauth");
    if (!token) {
      history.push("/login");
    }
  }
  const onload = async () => {
    const { data } = await fetchPortfolio();
    console.log(data, "portfol");
  };
  useEffect(async () => {
    const boole = await fetchPortfolio();
    if (boole.data[0].title1) {
      setLoad(1);
    }
    checkuser();
    onload();
  }, []);
  const handleSubmit = (e) => {
    const formData = new FormData();
    formData.append("photo", portfolioData.title);
    formData.append("birthdate", portfolioData.photo);
    createPortfolio(formData);
  };
  return load === 0 ? (
    <Loader />
  ) : (
    <div>
      <Navbar />
      <div class="container container_bottom">
        <h2>Set Your Projects Here</h2>

        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <input
            type="file"
            accept=".png, .jpg, .jpeg"
            name="photo"
            onChange={handlePhoto}
          />

          <input
            type="text"
            placeholder="name"
            name="title"
            value={newUser.name}
            onChange={handleChange}
          />

          <input type="submit" />
        </form>
      </div>
    </div>
  );
};

export default PortfolioAdmin;
