import React from "react";
import Navbar from "./Navbar";
import FileBase from "react-file-base64";
import "./dashboard.css";
import { useHistory } from "react-router-dom";

import { fetchPortfolio, createPortfolio } from "../../Api/index";
import { useState, useEffect } from "react";
import Loader from "../../Loader";

const PortfolioAdmin = () => {
  const history = useHistory();
  const [load, setLoad] = useState(0);
  const [portfolioData, setPortfolioData] = useState({
    title: "",
    photo: "",
  });

  const [Portfol, setPortfol] = useState();

  function checkuser() {
    const token = localStorage.getItem("jwtauth");
    if (!token) {
      history.push("/login");
    }
  }
  const onload = async () => {
    const { data } = await fetchPortfolio();
    setPortfol(data);
  };
  useEffect(async () => {
    const boole = await fetchPortfolio();
    if (boole.data) {
      setLoad(1);
    }
    checkuser();
    onload();
  }, []);
  const handleSubmit = (e) => {
    createPortfolio(portfolioData);
  };
  return load === 0 ? (
    <Loader />
  ) : (
    <div>
      <Navbar />

      <div className="container">
        {Portfol.map(() => (
          <div className="card mb-3" style={{ maxWidth: "540px" }}>
            <div className="row g-0">
              <div className="col-md-4">
                <img src="..." alt="..." />
              </div>
              <div className="col-md-8">
                <div className="card-body">
                  <h5 className="card-title">Card title</h5>
                  <p className="card-text">
                    This is a wider card with supporting text below as a natural
                    lead-in to additional content. This content is a little bit
                    longer.
                  </p>
                  <p className="card-text">
                    <small className="text-muted">
                      Last updated 3 mins ago
                    </small>
                  </p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div class="container container_bottom">
        <h2>Set Your Projects Here</h2>

        <form autoComplete="off" noValidate onSubmit={handleSubmit}>
          <div class="col-sm card port_margin">
            <img src={portfolioData.photo} class="img-responsive" />
            <div class="col-sm pro">
              <FileBase
                type="file"
                multipla={false}
                onDone={({ base64 }) =>
                  setPortfolioData({ ...portfolioData, photo: base64 })
                }
              />
            </div>
            <input
              type="text"
              class="form-control"
              placeholder="Titile of your project"
              aria-label="Sizing example input"
              aria-describedby="inputGroup-sizing-sm"
              value={portfolioData.title1}
              onChange={(e) => {
                setPortfolioData({
                  ...portfolioData,
                  title: e.target.value,
                });
              }}
            />
          </div>

          <div class="col-sm  button_submit">
            <button
              type="submit"
              class="btn btn-secondary btn-lg button_submit_b"
            >
              Set
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default PortfolioAdmin;
